#include&lt;iostream&gt;

namespace pattern{
	/// this pattern is facade pattern
	/// facade pattern is for hiding relationship and behavior of inner classes .
	/// client or outer class should only connect with facade class.
	/// But it will not forbid accessing inner classes.
#ifndef INNERA_H
#define INNERA_H
	class Inner_A{
	public:
		void Do(){std::cout&lt;&lt;"A"&lt;&lt;std::endl;}
	};
#endif
#ifndef INNERB_H
#define INNERB_H
	class Inner_B{
	public:
		void Do(){std::cout&lt;&lt;"B"&lt;&lt;std::endl;}
	};
#endif

#ifndef FACADE_H
#define FACADE_H
	class Facade{
	private:
		Inner_A* a;
		Inner_B* b;
	public:
		Facade(){
			a = new Inner_A();
			b = new Inner_B();

		}
		void Do(){
			a-&gt;Do();
			b-&gt;Do();
		}
		~Facade(){
			delete a;
			delete b;
		}
	};
#endif
}

int main()
{
	pattern::Facade f;
	f.Do();
	return 0;
}{% endcodeblock %}

